        DateUtil.getConvertDate2(F.nullCheck(param.get("enddate"), "")).replaceAll("-", "");

    // rownum 게시판 뿌리기 순서는 날짜
    sql.append(
        "SELECT          x.seq                                                                                                  \n");
    sql.append(
        "           ,x.brd_no                                                                                               \n");
    sql.append(
        "           ,x.brd_type                                                                                             \n");
    sql.append(
        "           ,x.title                                                                                                \n");
    sql.append(
        "           ,x.contents                                                                                             \n");
    sql.append(
        "           ,x.cno                                                                                                  \n");
    sql.append(
        "           ,x.reg_date                                                                                             \n");
    sql.append(
        "           ,x.hit                                                                                                  \n");
    sql.append(
        "           ,x.comment_cnt                                                                                          \n");
    sql.append(
        "           ,x.cpass                                                                                                \n");
    sql.append(
        "           ,x.file_cnt                                                                                             \n");
    sql.append(
        "FROM(                                                                                                              \n");
    sql.append(
        "    SELECT         rownum as seq                                                                                   \n");
    sql.append(
        "                  ,a.brd_no                                                                                        \n");
    sql.append(
        "                  ,a.brd_type                                                                                      \n");
    sql.append(
        "                  ,a.title                                                                                         \n");
    sql.append(
        "                  ,a.contents                                                                                      \n");
    sql.append(
        "                  ,a.cno                                                                                           \n");
    sql.append(
        "                  ,a.reg_date                                                                                      \n");
    sql.append(
        "                  ,a.hit                                                                                           \n");
    sql.append(
        "                  ,a.comment_cnt                                                                                   \n");
    sql.append(
        "                  ,a.cpass                                                                                         \n");
    sql.append(
        "                  ,a.file_cnt                                                                                      \n");
    sql.append(
        "    FROM(                                                                                                          \n");
    sql.append(
        "         SELECT        brd_no                                                                                      \n");
    sql.append(
        "                      ,brd_type                                                                                    \n");
    sql.append(
        "                      ,title                                                                                       \n");
    sql.append(
        "                      ,contents                                                                                    \n");
    sql.append(
        "                      ,cno                                                                                         \n");
    sql.append(
        "                      ,reg_date                                                                                    \n");
    sql.append(
        "                      ,hit                                                                                         \n");
    sql.append(
        "                      ,(SELECT COUNT(*) FROM T_SCM_QNA_BRD WHERE brd_no=t.brd_no) as comment_cnt                   \n");
    sql.append(
        "                      ,cpass                                                                                       \n");
    sql.append(
        "                      ,(SELECT COUNT(*) FROM T_SCM_FILE_BRD WHERE brd_no = t.brd_no) as file_cnt                   \n");
    sql.append(
        "         FROM          T_SCM_BRD t                                                                                 \n");
    sql.append(
        "         WHERE         brd_type='1'                                                                                \n");
    if (!"".equals(title)) { //
      sql.append(
          "   AND           title       LIKE                                                                            '%");
      sql.append(title);
      sql.append(
          "%'                                                                                                                 ");
    }

    if (!"".equals(s_contents)) { //
      sql.append(
          "   AND           contents    LIKE                                                                            '%");
      sql.append(s_contents);
      sql.append(
          "%'                                                                                                                 ");
    }

    if (!"".equals(startdate)) {
      sql.append(
          "   AND           TO_CHAR(reg_date, 'YYYYMMDD')>=                                                              '");
      sql.append(startdate);
      sql.append(
          "'                                                                                                              ");
    }

    if (!"".equals(enddate)) {
      sql.append(
          "   AND           TO_CHAR(reg_date, 'YYYYMMDD')<=                                                              '");
      sql.append(enddate);
      sql.append(
          "'                                                                                                                  ");
    }

    sql.append(
        "         ORDER BY      reg_date desc)a)x                                                                           \n");
    /*
     * sql.
     * append("  WHERE      rownum >=                                                                                           \n"
     * ); sql.append(start); sql.
     * append("  AND        rownum <=                                                                                           \n"
     * ); sql.append(end); sql.
     * append("                     )x                                                                                          \n"
     * );
     */
    sql.append(
        "WHERE      seq                                                                                                     \n");
    sql.append(
        "BETWEEN                                                                                                                \n");
    sql.append(start);
    sql.append(
        "                                                                                                                   \n");
    sql.append(
        "AND                                                                                                                    \n");
    sql.append(end);
    sql.append(
        "                                                                                                                   \n");
    sql.append(
        "ORDER BY seq                                                                                                         ");

    System.out.println(sql.toString());

    return (List<NotiDTO>) this.jdbcTemplate.query(sql.toString(), this.mapper3);

